import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'
import loginImg from "./team.png";

class registerFront extends Component {
    constructor(){
        super()
        this.state = {
            email:'',
            password:''
        }
        this.changeEmail = this.changeEmail.bind(this)
        this.changePassword = this.changePassword.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }



    changeEmail(event){
        this.setState({
            email:event.target.value
        })
    }
    changePassword(event){
        this.setState({
            password:event.target.value
        })
    }

    onSubmit(event){
        event.preventDefault()

        const registered = {
            email: this.state.email,
            password: this.state.password
        }

        axios.post('http://localhost:4000/user/signup', registered)
            .then(response => console.log(response.data))
        // window.location = '/'
        this.setState({
            email:'',
            password:''
        })
    }

    render() {
        return ( 
            <div>
                <div className='container'>
                    <div className='form-div'>
                        <form onSubmit={this.onSubmit}>

                            <input type='text'
                            placeholder='Email'
                            onChange={this.changeEmail}
                            value={this.state.email}
                            className='form-control form-group'
                            />

                            <input type='password'
                            placeholder='Password'
                            onChange={this.changePassword}
                            value={this.state.password}
                            className='form-control form-group'
                            />


                            <input type='submit' className='btn btn-danger btn-block' value='Sign Up' />
                        </form>
                    </div>
                </div>

            </div>
        );
    }

    render() {
        return (
          <div className="base-container" >
            <div className="header">Register</div>
            <div className="content">
              <div className="image">
                < img src={loginImg} />
              </div>
              <div className="form">
                <div className="form-group">
                  <form onSubmit={this.onSubmit}>
                    <input type='text'
                    placeholder='Email'
                    onChange={this.changeEmail}
                    value={this.state.email}
                    className='form-control form-group'
                    />

                    <input type='password'
                    placeholder='Password'
                    onChange={this.changePassword}
                    value={this.state.password}
                    className='form-control form-group'
                    />
                    <input type='submit' className='btn btn-danger btn-block' value='Sign Up' />
                  </form>
                </div>
              </div>
            </div>
          </div>
        );
    }
}

export default registerFront;
